def get_foreign_capital_history_holding(exchange, holding_date=date.today()):
    month = holding_date.month
    day = holding_date.day
    logging.warning(
        f"爬取外资持股数据...\texchange:{exchange}\tholding_date:{holding_date}")
    base_url = f"https://www.hkexnews.hk/sdw/search/mutualmarket_c.aspx?t={exchange}"
    driver = webdriver.Chrome(options=chrome_options)
    driver.get(base_url)
    driver.find_element_by_id("txtShareholdingDate").click()
    driver.find_element_by_xpath(
        '//*[@id="date-picker"]/div[1]/b[1]/ul/li[1]/button').click()
    driver.find_element_by_xpath(
        f'//*[@id="date-picker"]/div[1]/b[2]/ul/li[{month}]/button').click()
    driver.find_element_by_xpath(
        f'//*[@id="date-picker"]/div[1]/b[3]/ul/li[{day}]/button').click()
    driver.find_element_by_id("btnSearch").click()
    html = driver.page_source
    soup = BeautifulSoup(html, 'html.parser').select(
        'div[class="mobile-list-body"]')
    data = [i.text for i in soup]
    driver.close()
    # print(data)
    fc_data = []
    for code, name, count, ratio in zip(range(0, len(data), 4), range(
            1, len(data), 4), range(2, len(data), 4), range(3, len(data), 4)):
        tmp = {
            'code': data[code], 'name': cc.convert(data[name]),
            'holdingCount': int(data[count].replace(',', '')), 'holdingRatio': float(data[ratio].replace('%', ''))}
        fc_data.append(tmp)
    for i in STOCK_LIST:
        for j in fc_data:
            if i['name'] == j['name']:
                j['code'] = i['code']
    return fc_data


def FC_history_Query(holding_date=date.today()-timedelta(days=1)):
    # 外资历史持股数据查询
    exchanges = ['sh', 'sz']
    fc_total = []
    logging.warning(f"外资持仓记录查询")
    for i in exchanges:
        data = get_foreign_capital_history_holding(
            exchange=i, holding_date=holding_date)
        fc_total += data
    fc_total = [i for i in fc_total if i['code'][0] in ('0', '3', '6') and i['holdingRatio'] >= 0.5]
    for i in fc_total:
        del i['holdingCount']
        del i['holdingRatio']
    print(f"{len(fc_total)}\t{fc_total}")
    return fc_total


def get_funding_holding(year=2021, quarter=4):
    pool = []
    data = ts.fund_holdings(year=year, quarter=quarter)
    for i in data.values:
        code = f"{i[7]}.SH" if i[7].startswith('6') else f"{i[7]}.SZ"
        name = i[3]
        fundHoldingdRatio = float(i[6])
        if fundHoldingdRatio >= 2 and code[0] in ('0', '3', '6'):
            pool.append({'code': code, 'name': name})
    print(f"\n{len(pool)}\t{pool}")
    return pool


